import { Component, OnInit } from '@angular/core';
import { AuthorService } from '../../services/author.service';
import { Author } from '../../interfaces/author.interface';

@Component({
  selector: 'app-list-page',
  templateUrl: './list-page.component.html',
  styleUrls: ['./list-page.component.css']
})
export class ListPageComponent implements OnInit{

  public authors: string[] = [];

  constructor(private authorService:AuthorService){}


  ngOnInit(): void {
    this.authorService.getAuthors()
    .subscribe(authors => this.authors = authors.authors);
  }

  onTilles(author: string): void {


  }


}

<div class="d-flex justify-content-between">
    <div  >
        <ul class="list-group" *ngFor="let author of authors, let i = index ">

            <li class="list-group-item ml-2 ">
                <span class="p-3">{{i}}</span>
                {{author}}



                <div>
                    <button     [routerLink]="['/author', author]"
                    >
                    Obras</button>
                    <button type="button" class="btn btn-outline-danger">Agregar
                        Favoritos
                    </button>
                </div>



            </li>


          </ul>

    </div>
</div>
