import { Component, OnInit } from '@angular/core';
import { Book } from '../../interfaces/title.interface';
import { AuthorService } from '../../services/author.service';
import { ActivatedRoute, Router } from '@angular/router';
import { switchMap } from 'rxjs';

@Component({
  selector: 'app-title-page',
  templateUrl: './title-page.component.html',
  styleUrls: ['./title-page.component.css']
})
export class TitlePageComponent implements OnInit{

  constructor(private authorService: AuthorService,
    private activatedRoute: ActivatedRoute,
    private router: Router){}

  public books: Book[] = [];

  ngOnInit(): void {

    this.activatedRoute.params
    .pipe (
      switchMap(({id}) =>   
        this.authorService.getBook(id) 

      ),

    ).subscribe(



      book => {

        if(!book) return this.router.navigate(['/author/list']);

        this.books = book
        console.log(book)
        return;
      }
    )

  }

  goBack():void{
    this.router.navigateByUrl('author/list');
  }




}


<button
mat-button
color="warn"
(click)="goBack()"
>
    Regresar
</button>

<div class="d-flex ">

  <div *ngFor="let book of books" class="card" style="width: 18rem;">

    <div class="card-body">
      <h5 class="card-title">
        {{book.title}}
      </h5>
      <h6 class="card-subtitle mb-2 text-body-secondary">
      {{book.author}}  </h6>
      <p class="card-text">
        {{book.lines}}
      </p>


      <a href="#" class="card-link">Card link</a>
      <a href="#" class="card-link">Another link</a>
    </div>

</div>


</div>
